SELECT
    user.id,
    team_user.team_id,
    user.password_hash,
    team.name,
    IF(team_user.team_id = value, 1, 0) AS last
FROM team
INNER JOIN team_user
ON team.id = team_user.team_id
INNER JOIN user
ON team_user.user_id = user.id
LEFT JOIN user_data
ON user_data.user_id = user.id AND `key` = 'last_team'
WHERE email = '{{email}}';
