SELECT
    user.id AS user_id
FROM
    user
INNER JOIN
    team_user
ON
    team_user.user_id = user.id
WHERE
    user_id = {{user_id}} AND team_id = {{team_id}}

UNION

SELECT
    {{user_id}} as user_id
FROM team
WHERE
    id = {{team_id}} AND
    (
        SELECT
            1
        FROM
            user
        WHERE
            user.id = {{user_id}} AND
            user.is_super = 1
    );
