SELECT DISTINCT campaign.content_id
FROM campaign
WHERE
    campaign.content_id = '{{content_id}}' AND
{{#each cats}}
    {{#if .}}
        campaign.cat{{inc @index}} = '{{.}}' AND
    {{/if}}
{{/each}}
    campaign.team_id = {{team_id}} AND
    campaign.finished = 1 AND
    {{to}} > campaign.created_at AND
    {{to}} < campaign.end_time;
