SELECT
    count(1) as count
FROM
    campaign
WHERE
    {{#if content_id}}
        campaign.content_id = '{{content_id}}' AND
    {{else}}
        {{#each cats}}
            {{#if .}}
                campaign.cat{{inc @index}} = '{{.}}' AND
            {{/if}}
        {{/each}}
    {{/if}}
    campaign.team_id = {{team_id}} AND
    campaign.finished = 1;
