SELECT
    count(1) as count
FROM
    campaign
RIGHT JOIN
    team_user_fb_asset as a
ON
    a.team_id = {{team_id}} AND
    a.user_id = {{user_id}} AND
    a.`type` = 2 AND
    a.`value` = campaign.adaccount
WHERE
    {{#if content_id}}
        campaign.content_id = '{{content_id}}' AND
    {{else}}
        {{#each cats}}
            campaign.cat{{inc @index}} = {{.}} AND
        {{/each}}
    {{/if}}
    campaign.team_id = {{team_id}} AND
    campaign.finished = 1;
