SELECT
    t1.value AS 'value',
    t1.display_value AS 'display',
    t2.display_value AS 'child_display',
    t2.value AS 'child'
FROM
    team_property t1
LEFT JOIN
    team_property t2
ON
    t1.id = t2.parent_id AND
    t2.team_id = {{team_id}} AND
    t2.name = '{{children}}'
WHERE 
    t1.team_id = {{team_id}} AND
    t1.name = '{{name}}';
