var util = {};

util.extend = function (cats) {
    [0, 1, 2].forEach(function (i) {
        if (typeof cats[i] === 'undefined') {
            cats.push('NONE');
        }
    });
};

util.compare = function (cats1, cats2) {
    var result = true;

    util.extend(cats1);
    util.extend(cats2);

    [0, 1, 2].forEach(function (i) {
        if (cats1[i] !== cats2[i]) {
            result = false;
        }
    });

    return result;
};

module.exports = util;
