var utils = {};

utils.stringToMatrix = function (input, separator) {
    function split (input, sep) {
        var ret = input.split(sep),
            i = 0;

        for (; i < ret.length; i += 1) {
            if (sep.slice(0, -1)) {
                ret[i] = split(ret[i], sep.slice(0, -1));
            }
        }

        return ret;
    }

    if (input === 'None') {
        return null;
    }

    return split(input, separator);
};

module.exports = utils;
