var resources = require('@lib/resources');

function handleRequest (event, context, logger, promiseGenerator) {
    var local = {
            'now': Math.floor(Date.now() / 1000)
        },
        r;

    return resources.get().then(function (resources) {
        r = resources;
        return promiseGenerator(local, r.pool);
    }).then(function (response) {
        local.res = response;
    }).catch(function (err) {
        local.err = err;
        logger.error(err);
    }).finally(function () {
        return resources.dispose(r).then(function () {
            if (local.err) {
                return context.fail(local.err);
            }
            return context.succeed(local.res);
        });
    });
}

module.exports = handleRequest;
