var resources = require('@lib/resources'),
    Facebook = require('@lib/model/facebook'),
    sessionModel = require('@lib/model/session'),
    authModel = require('@lib/model/auth');

function handleRequest (event, context, logger, promiseGenerator) {
    var local = {
            'now': Math.floor(Date.now() / 1000)
        },
        r;

    return resources.get().then(function (resources) {
        r = resources;
        return sessionModel.get(event.session_id, local.now, r.pool);
    }).then(function (session) {
        local.session = session;
        return authModel.getFilters(
            local.session.team_id,
            local.session.user_id,
            r.pool
        );
    }).then(function (cats) {
        local.cats = cats;

        return Facebook.getAccessToken(local.session.user_id, r.pool);
    }).then(function (token) {
        local.fb = {
            'token': token
        };

        return Facebook.getAdAccount(
            local.session.team_id,
            local.session.user_id,
            r.pool
        );
    }).then(function (adAccount) {
        if (!adAccount) {
            throw 'model/adaccount:400:Ad Account is required.';
        }
        local.fb.ad_account = adAccount;

        return promiseGenerator(local, r.pool);
    }).then(function (response) {
        local.res = response;
    }).catch(function (err) {
        local.err = err;
        logger.error(err);
    }).finally(function () {
        return resources.dispose(r).then(function () {
            if (local.err) {
                return context.fail(local.err);
            }
            return context.succeed(local.res);
        });
    });
}

module.exports = handleRequest;
