var dynamodb = require('@lib/resources/dynamodb'),
    Kinesis = require('@lib/resources/kinesis');

function handleRequest (event, context, logger, promiseGenerator) {
    var local = {
            'now': Date.now()
        },
        r = {};

    return dynamodb.init().then(function (db) {
        r.db = db;
        return Kinesis.init();
    }).then(function (kinesis) {
        r.kinesis = kinesis;
        return promiseGenerator(local, r);
    }).then(function (response) {
        local.res = response;
    }).catch(function (err) {
        local.err = err;
        logger.error(err);
    }).finally(function () {
        if (local.err) {
            return context.fail(local.err);
        }
        return context.succeed(local.res);
    });
}

module.exports = handleRequest;
