SELECT
    `team_id`,
    `team_user_fb_asset`.`user_id`,
    `type`,
    `team_user_fb_asset`.`value` as 'asset_id',
    `user_data`.`value` as 'access_token'
FROM
    `team_user_fb_asset`
RIGHT JOIN
    `user_data`
ON
    `user_data`.`user_id` = `team_user_fb_asset`.`user_id` AND
    `user_data`.`key` = 'facebook_access_token'
WHERE
    `type` = 2 AND
    `is_crawler` = 1 AND
    `team_id` = 5
GROUP BY `type`, `team_user_fb_asset`.`value`;
