var path = require('path'),
    sqlUtils = require('@lib/utils/sql'),
    db = require('@lib/resources/db'),
    q = sqlUtils.getQueries(path.join(__dirname, 'sql')),
    Promise = require('bluebird'),
    cpa = require(
        path.join(__dirname, '..', 'metrics', 'cpa')
    );

module.exports.handler = function (event, context) {
    var rulePromises = event.rules.reduce(function (prev, act) {
        var params = {
            'team_id': event.team_id,
            'to': event.timestamp,
            'cats': [event.cat1, event.cat2, event.cat3],
            'rule_id': act.id
        };

        prev[act.id] = db.q(
            q['promoted'],
            params,
            context.pool
        );

        return prev;
    }, {});

    return Promise.join(
        cpa.get(
            event,
            context
        ),
        Promise.props(rulePromises),
        function (cpaList, ruleData) {
            return Object.keys(ruleData).reduce(function (prev, ruleId) {
                prev[ruleId] = cpaList.filter(function (act) {
                    return !ruleData[ruleId].filter(function (act2) {
                        return act.content_id === act2.content_id;
                    })[0];
                }).map(function (act) {
                    return {
                        'id': act.content_id,
                        'cpa': act.cpa,
                        'promotion_id': null,
                        'campaign_id': null
                    };
                });
                return prev;
            }, {});
        }
    );
};
