SELECT  
    content_id,
    promotion_id,
    campaign_id
FROM rule_action
WHERE until = 0 AND
    team_id = {{team_id}} AND
    action_type = 'promotion' AND
{{#each cats}}
        cat{{inc @index}} = '{{.}}' AND
{{/each}}
{{#if rule_id}}
    rule_id = {{rule_id}} AND
{{/if}}
    {{to}} < expire;
