var path = require('path'),
    sqlUtils = require('@lib/utils/sql'),
    db = require('@lib/resources/db'),
    q = sqlUtils.getQueries(path.join(__dirname, 'sql')),
    Promise = require('bluebird'),
    toplist = require('@lib/model/toplist');

module.exports.handler = function (event, context) {
    var rulePromises = event.rules.reduce(function (prev, act) {
        var params = {
            'team_id': event.team_id,
            'to': event.timestamp,
            'rule_id': act.id
        };

        prev[act.id] = db.q(
            q['promoted'],
            params,
            context.pool
        );

        return prev;
    }, {});

    return Promise.join(
        toplist.get(
            event.team_id,
            [event.cat1, event.cat2, event.cat3],
            event.timestamp
        ),
        Promise.props(rulePromises),
        function (tl, ruleData) {
            return Object.keys(ruleData).reduce(function (prev, ruleId) {
                prev[ruleId] = tl.filter(function (act) {
                    return !ruleData[ruleId].filter(function (act2) {
                        return act.contentId === act2.content_id;
                    })[0];
                }).map(function (act) {
                    return {
                        'id': act.contentId,
                        'momentum': act.momentum * 100,
                        'promotion_id': null,
                        'campaign_id': null
                    };
                });
                return prev;
            }, {});
        }
    );
};
