var path = require('path'),
    sqlUtils = require('@lib/utils/sql'),
    db = require('@lib/resources/db'),
    q = sqlUtils.getQueries(path.join(__dirname, 'sql')),
    Promise = require('bluebird'),
    toplist = require('@lib/model/toplist');

module.exports.handler = function (event, context) {
    var params = {
        'team_id': event.team_id,
        'cats': [event.cat1, event.cat2, event.cat3],
        'to': event.timestamp
    };

    return Promise.join(
        toplist.get(
            params.team_id,
            params.cats,
            params.to
        ),
        db.q(
            q['shared'],
            params,
            context.pool
        ),
        function (toplist, shared) {
            return toplist.filter(function (act) {
                return !shared.filter(function (act2) {
                    return act.contentId === act2.content_id;
                })[0];
            }).map(function (act) {
                return {
                    'id': act.contentId,
                    'momentum': act.momentum * 100,
                    'promotion_id': null,
                    'campaign_id': null
                };
            });
        }
    );
};
